import React from 'react';

const Card = ({ imageUrl, name, description, githubLink }) => {
  const cardStyle = {
    position: 'relative',
    width: '300px', // Adjust the width as needed
    border: '1px solid #ccc', // Add border for card styling
  };

  const imageStyle = {
    width: '100%',
  };

  const buttonStyle = {
    background: 'gray', // Change this to your desired button color
    color: 'white', // Change this to your desired text color
    padding: '10px 20px',
    borderRadius: '0 0 5px 5px', // Rounded corners at the bottom
    textDecoration: 'none',
    position: 'absolute',
    bottom: 0,
    width: '100%',
  };

  return (
    <div className="card" style={cardStyle}>
      <img src={imageUrl} alt={name} style={imageStyle} />
      <h2>{name}</h2>
      <p>{description}</p>
      <a href={githubLink} target="_blank" rel="noopener noreferrer" style={buttonStyle}>
        GitHub
      </a>
    </div>
  );
};

export default Card;
