import React, { useEffect, useState } from 'react';
import './Home.css';

function Home() {
  const [showWelcome, setShowWelcome] = useState(false);
  const [welcomeText, setWelcomeText] = useState('');

  useEffect(() => {
    const welcomeMessage = 'Welcome, my name is Najel!';
    let currentIndex = -1;
    let animationFrameId;

    const typeText = () => {
      if (currentIndex < welcomeMessage.length) {
        setWelcomeText((prevText) => prevText + welcomeMessage.charAt(currentIndex));
        currentIndex++;
        animationFrameId = requestAnimationFrame(typeText);
      }
    };

    setShowWelcome(true);
    animationFrameId = requestAnimationFrame(typeText);

    return () => {
      cancelAnimationFrame(animationFrameId);
      setWelcomeText('');
    };
  }, []);

  // Reset the welcomeText when the component mounts again
  useEffect(() => {
    setWelcomeText('');
  }, []);

  return (
    <div className="home">
      {showWelcome && <div className="welcome-text">{welcomeText}</div>}
      {/* Filler content to scroll down */}
      <section>
            <h1>Welcome to My Website</h1>
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. ...</p>
          </section>
          <section>
            <h2>About Me</h2>
            <p>Curabitur commodo, ipsum nec rutrum scelerisque, ...</p>
          </section>
    </div>
  );
}

export default Home;
