import React, { useEffect, useState } from 'react';
import './Footer.css';
import { Button } from './Button';
import { Link } from 'react-router-dom';

function Footer() {
  const [footerVisible, setFooterVisible] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const windowHeight = window.innerHeight;
      const documentHeight = document.documentElement.scrollHeight;

      if (scrollPosition + windowHeight >= documentHeight) {
        setFooterVisible(true);
      } else {
        setFooterVisible(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const footerClass = footerVisible ? 'footer-container footer-visible' : 'footer-container';

  return (
    <div className={footerClass}>
      <section className='social-media'>
        <div className='social-media-wrap'>
          <div className='social-icons'>

            <Link
              className='social-icon-link github'
              to='https://github.com/Najel-A'
              target='_blank'
              aria-label='Youtube'
            >
              <i className='fab fa-github' />
            </Link>

            <Link
              className='social-icon-link'
              href='mailto:nlalarco@ucsc.edu'
              target='_blank'
              aria-label='Mail'
            >
              <i className='fab fa-envelope' />
            </Link>

            <Link
              className='social-icon-link linkedin'
              to='https://www.linkedin.com/in/najel-alarcon/'
              target='_blank'
              aria-label='LinkedIn'
            >
              <i className='fab fa-linkedin' />
            </Link>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Footer;
