import React, { useEffect, useState } from 'react';

function Home() {
  const [text, setText] = useState('');

  useEffect(() => {
    const welcomeText = 'Welcome';
    const typingDelay = 100; // Delay between each character typing
    const typingSpeed = 5; // Time delay in seconds before starting typing

    const timer = setTimeout(() => {
      let currentIndex = 0;
      const interval = setInterval(() => {
        setText((prevText) => prevText + welcomeText[currentIndex]);
        currentIndex++;

        if (currentIndex === welcomeText.length) {
          clearInterval(interval);
        }
      }, typingDelay);
    }, typingSpeed * 1000);

    return () => clearTimeout(timer);
  }, []);

  const styles = {
    background: 'black',
    width: '100vw',
    height: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    color: 'white',
    fontSize: '3rem',
  };

  return (
    <div style={styles}>
      <h1>{text}</h1>
    </div>
  );
}

export default Home;
