import React, { useEffect, useState } from 'react';
import './Home.css';

function Home() {
  const [showWelcome, setShowWelcome] = useState(false);
  const [welcomeText, setWelcomeText] = useState('');

  useEffect(() => {
    const welcomeMessage = 'Welcome, my name is Najel!';

    const typeText = () => {
      for (let i = 0; i < welcomeMessage.length; i++) {
        setWelcomeText((prevText) => prevText + welcomeMessage.charAt(i));
      }
    };

    setShowWelcome(true);
    typeText();

    return () => {
      setWelcomeText('');
    };
  }, []);

  // Reset the welcomeText when the component mounts again
  useEffect(() => {
    setWelcomeText('');
  }, []);

  return (
    <div className="home">
      {showWelcome && <div className="welcome-text">{welcomeText}</div>}
      {/* Other content */}
    </div>
  );
}

export default Home;
