import React, { useEffect, useState } from 'react';

function Home() {
  const [text, setText] = useState('');
  const welcomeText = 'Welcome!';
  const typingDelay = 100; // Delay between each character typing
  const erasingDelay = 100; // Delay between erasing each character
  const newTextDelay = 7000; // Delay before starting the next cycle

  useEffect(() => {
    const handleTyping = () => {
      setText((prevText) => welcomeText.slice(0, prevText.length + 1));
    };

    const handleErasing = () => {
      setText((prevText) => welcomeText.slice(0, prevText.length - 1));
    };

    let timer = setTimeout(handleTyping, typingDelay);
    setTimeout(() => {
      clearTimeout(timer);
      timer = setTimeout(handleErasing, erasingDelay);
    }, newTextDelay);

    return () => clearTimeout(timer);
  }, [text]);

  const styles = {
    background: "black",
    width: "100vw",
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    color: "white",
    fontSize: "3rem",
  };

  const responsiveStyles = {
    fontSize: "2rem",
  };

  return (
    <div style={styles}>
      <h1 style={window.innerWidth <= 768 ? responsiveStyles : {}}>{text}</h1>
    </div>
  );
}

export default Home;
