import React from 'react';

const Card = ({ imageUrl, name, description, githubLink }) => {
  const buttonStyle = {
    background: 'gray', // Change this to your desired button color
    color: 'white', // Change this to your desired text color
    padding: '10px 20px',
    borderRadius: '5px',
    textDecoration: 'none',
    marginTop: "50px"
  };

  return (
    <div className="card">
      <img src={imageUrl} alt={name} height={250} />
      <h2>{name}</h2>
      <p>{description}</p>
      <p><a href={githubLink} target="_blank" rel="noopener noreferrer" style={buttonStyle}>
        GitHub
      </a></p>
      
    </div>
  );
};

export default Card;
