import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const [click, setClick] = useState(false);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      const yOffset = -80; // Offset for any fixed navigation bar or header
      const y = section.getBoundingClientRect().top + window.pageYOffset + yOffset;

      window.scrollTo({ top: y, behavior: 'smooth' });
      closeMobileMenu();
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll('.section');

      sections.forEach((section) => {
        const sectionTop = section.offsetTop - 100; // Offset to trigger active state before reaching the section
        const sectionBottom = sectionTop + section.offsetHeight;

        const scrollPosition = window.pageYOffset;

        if (scrollPosition >= sectionTop && scrollPosition < sectionBottom) {
          const sectionId = section.id;
          const navLinks = document.querySelectorAll('.nav-links');

          navLinks.forEach((link) => {
            if (link.getAttribute('data-section-id') === sectionId) {
              link.classList.add('active');
            } else {
              link.classList.remove('active');
            }
          });
        }
      });
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
      <nav className='navbar'>
        <div className='navbar-container'>
          <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
            Najel <i className="fa-solid fa-bowl-rice fa-fade"></i>
          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <button
                className={`nav-links ${click ? 'active' : ''}`}
                onClick={() => scrollToSection('home')}
                data-section-id="home"
              >
                Home
              </button>
            </li>
            <li className='nav-item'>
              <button
                className={`nav-links ${click ? 'active' : ''}`}
                onClick={() => scrollToSection('about')}
                data-section-id="about"
              >
                About
              </button>
            </li>
            <li className='nav-item'>
              <button
                className={`nav-links ${click ? 'active' : ''}`}
                onClick={() => scrollToSection('work')}
                data-section-id="work"
              >
                Work
              </button>
            </li>
            <li>
              <Link to='/contact' className='nav-links-mobile' onClick={closeMobileMenu}>
                Contact
              </Link>
            </li>
          </ul>
          {/* Other elements */}
        </div>
      </nav>
      <div id="home" className="section">
        <h1>Home Section</h1>
      </div>
      <div id="about" className="section">
        <h1>About Section</h1>
      </div>
      <div id="work" className="section">
        <h1>Work Section</h1>
      </div>
    </>
  );
}

export default Navbar;
