import React, { useEffect, useState } from 'react';

function Home() {
  const [text, setText] = useState('');
  const welcomeText = 'Hello';
  const typingDelay = 125; // Delay between each character typing
  const erasingDelay = 125; // Delay between erasing each character
  const newTextDelay = 2000; // Delay before starting the next cycle

  useEffect(() => {
    const handleTyping = () => {
      setText((prevText) => {
        if (prevText.length < welcomeText.length) {
          return welcomeText.slice(0, prevText.length + 1);
        } else {
          return prevText;
        }
      });
    };

    const handleErasing = () => {
      setText((prevText) => {
        if (prevText.length > 0) {
          return welcomeText.slice(0, prevText.length - 1);
        } else {
          return prevText;
        }
      });
    };

    let timer = setTimeout(handleTyping, typingDelay);
    setTimeout(() => {
      clearTimeout(timer);
      timer = setTimeout(handleErasing, erasingDelay);
    }, newTextDelay);

    return () => clearTimeout(timer);
  }, [text]);

  const styles = {
    background: 'black',
    width: '100vw',
    height: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    color: 'white',
    fontSize: '3rem',
  };

  const responsiveStyles = {
    fontSize: '2rem',
  };

  return (
    <div>
      <section>
        <h1>Welcome to My Website</h1>
        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. ...</p>
      </section>
      <section>
        <h2>About Me</h2>
        <p>Curabitur commodo, ipsum nec rutrum scelerisque, ...</p>
      </section>
      {/* Add more sections or components as needed */}
    </div>
  );
}

export default Home;
