import React, { useEffect, useState } from 'react';
import './Home.css';

function Home() {
  const [showWelcome, setShowWelcome] = useState(false);
  const [welcomeText, setWelcomeText] = useState('');

  useEffect(() => {
    const welcomeMessage = 'Welcome';

    const typeText = () => {
      for (let i = 0; i < welcomeMessage.length; i++) {
        setTimeout(() => {
          setWelcomeText((prevText) => prevText + welcomeMessage.charAt(i));
        }, i * 100); // Delay of 100 milliseconds between each character
      }
    };

    setTimeout(() => {
      setShowWelcome(true);
      typeText();
    }, 500); // Delay of 1 second before showing the welcome message

    return () => {
      setWelcomeText('');
    };
  }, []);

  return (
    <div className="home">
      {showWelcome && <div className="welcome-text">{welcomeText}</div>}
      {/* Other content */}
    </div>
  );
}

export default Home;
